// frontend/src/components/EquipmentTable.js - –ü–û–õ–ù–ê–Ø –ò–°–ü–†–ê–í–õ–ï–ù–ù–ê–Ø –í–ï–†–°–ò–Ø

import React, { useState, useEffect } from 'react';
import { toast } from 'react-toastify';
import { useAuth } from '../contexts/AuthContext';
import api from '../services/api';
import './EquipmentTable.css';
import {
    getEquipmentTypeText,
    getEquipmentTypeOptions
} from '../components/EquipmentTypes';

const EquipmentTable = ({ equipment, isOpen, onClose, onSave }) => {
    const { user } = useAuth();
    const [formData, setFormData] = useState({
        id: '',
        equipment_type: '',
        model: '',
        status: '',
        planned_hours: 0,
        malfunction: '',
        mechanic_name: ''
    });
    const [loading, setLoading] = useState(false);
    const [isSaving, setIsSaving] = useState(false); // ‚úÖ –î–û–ë–ê–í–õ–ï–ù–û
    const [history, setHistory] = useState([]);
    const [historyLoading, setHistoryLoading] = useState(false);
    const [activeTab, setActiveTab] = useState('edit');
    const [showIdChangeConfirm, setShowIdChangeConfirm] = useState(false);
    const [originalId, setOriginalId] = useState('');

    useEffect(() => {
        if (equipment) {
            const equipmentData = {
                id: equipment.id || '',
                equipment_type: equipment.equipment_type || '',
                model: equipment.model || '',
                status: equipment.status || '',
                planned_hours: equipment.planned_hours || 0,
                malfunction: equipment.malfunction || '',
                mechanic_name: equipment.mechanic_name || ''
            };

            setFormData(equipmentData);
            setOriginalId(equipment.id);

            if (user && (user.role === 'admin' || user.role === 'dispatcher' || user.role === 'programmer')) {
                loadHistory();
            }
        }
    }, [equipment, user]);

    const loadHistory = async () => {
        setHistoryLoading(true);

        try {
            const response = await api.get(`/equipment/${equipment.id}/history`);

            if (response.data && Array.isArray(response.data)) {
                setHistory(response.data);
            } else {
                console.warn('‚ö†Ô∏è –ò—Å—Ç–æ—Ä–∏—è –ø—Ä–∏—à–ª–∞ –Ω–µ –≤ –≤–∏–¥–µ –º–∞—Å—Å–∏–≤–∞:', response.data);
                setHistory([]);
            }
        } catch (error) {
            console.error('‚ùå Error loading history:', error);
            setHistory([]);

            if (error.response?.status === 404) {
                toast.info('–ò—Å—Ç–æ—Ä–∏—è –¥–ª—è —ç—Ç–æ–≥–æ –æ–±–æ—Ä—É–¥–æ–≤–∞–Ω–∏—è –ø–æ–∫–∞ –Ω–µ —Å–æ–∑–¥–∞–Ω–∞');
            } else if (error.response?.status === 500) {
                toast.error('–û—à–∏–±–∫–∞ —Å–µ—Ä–≤–µ—Ä–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ –∏—Å—Ç–æ—Ä–∏–∏');
            } else {
                toast.error('–ù–µ —É–¥–∞–ª–æ—Å—å –∑–∞–≥—Ä—É–∑–∏—Ç—å –∏—Å—Ç–æ—Ä–∏—é –∏–∑–º–µ–Ω–µ–Ω–∏–π');
            }
        } finally {
            setHistoryLoading(false);
        }
    };

    const handleIdChange = async () => {
        if (!formData.id || formData.id.trim() === '') {
            toast.error('ID –Ω–µ –º–æ–∂–µ—Ç –±—ã—Ç—å –ø—É—Å—Ç—ã–º');
            return;
        }

        if (formData.id === originalId) {
            toast.info('ID –Ω–µ –∏–∑–º–µ–Ω–∏–ª—Å—è');
            return;
        }

        try {
            setLoading(true);
            await api.put(`/equipment/${originalId}/change-id`, {
                newId: formData.id.trim()
            });

            toast.success('ID –æ–±–æ—Ä—É–¥–æ–≤–∞–Ω–∏—è –∏–∑–º–µ–Ω–µ–Ω —É—Å–ø–µ—à–Ω–æ!');
            setOriginalId(formData.id);
            setShowIdChangeConfirm(false);
            onSave();
        } catch (error) {
            console.error('Error changing ID:', error);
            toast.error(error.response?.data?.message || '–û—à–∏–±–∫–∞ –∏–∑–º–µ–Ω–µ–Ω–∏—è ID');
            setFormData(prev => ({ ...prev, id: originalId }));
        } finally {
            setLoading(false);
        }
    };

    const handlePlannedHoursChange = (e) => {
        const value = e.target.value.trim();

        if (value === '' || /^[\d.,]+$/.test(value)) {
            const normalizedValue = value.replace(',', '.');
            const numValue = parseFloat(normalizedValue);

            if (!isNaN(numValue) && numValue >= 0 && numValue <= 1000) {
                setFormData(prev => ({
                    ...prev,
                    planned_hours: numValue
                }));
            } else if (value === '') {
                setFormData(prev => ({
                    ...prev,
                    planned_hours: 0
                }));
            } else {
                if (!isNaN(numValue)) {
                    toast.warning('–ü–ª–∞–Ω–æ–≤–æ–µ –≤—Ä–µ–º—è –¥–æ–ª–∂–Ω–æ –±—ã—Ç—å –æ—Ç 0 –¥–æ 1000 —á–∞—Å–æ–≤');
                }
            }
        }
    };

    // ‚úÖ –ò–°–ü–†–ê–í–õ–ï–ù–ù–´–ô –º–µ—Ç–æ–¥ handleSubmit
    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!user || (user.role !== 'admin' && user.role !== 'dispatcher' && user.role !== 'programmer')) {
            toast.error('–ù–µ–¥–æ—Å—Ç–∞—Ç–æ—á–Ω–æ –ø—Ä–∞–≤ –¥–ª—è —Ä–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞–Ω–∏—è');
            return;
        }

        // ‚úÖ –í–∞–ª–∏–¥–∞—Ü–∏—è planned_hours
        const plannedHours = parseFloat(formData.planned_hours);

        if (isNaN(plannedHours) || plannedHours < 0 || plannedHours > 1000) {
            toast.error('–ü–ª–∞–Ω–æ–≤–æ–µ –≤—Ä–µ–º—è –¥–æ–ª–∂–Ω–æ –±—ã—Ç—å —á–∏—Å–ª–æ–º –æ—Ç 0 –¥–æ 1000 —á–∞—Å–æ–≤');
            return;
        }

        // ‚úÖ –ï—Å–ª–∏ ID –∏–∑–º–µ–Ω–∏–ª—Å—è - –ø–æ–∫–∞–∑—ã–≤–∞–µ–º –ø–æ–¥—Ç–≤–µ—Ä–∂–¥–µ–Ω–∏–µ
        if (formData.id !== originalId) {
            setShowIdChangeConfirm(true);
            return;
        }

        // ‚úÖ –°–æ—Ö—Ä–∞–Ω—è–µ–º –¥–∞–Ω–Ω—ã–µ
        await saveEquipmentData();
    };

    // ‚úÖ –ò–°–ü–†–ê–í–õ–ï–ù–ù–´–ô –º–µ—Ç–æ–¥ saveEquipmentData
    const saveEquipmentData = async () => {
        // ‚úÖ –ü—Ä–µ–¥–æ—Ç–≤—Ä–∞—â–∞–µ–º –¥–≤–æ–π–Ω–æ–µ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏–µ
        if (isSaving) {
            console.log('‚è≥ –°–æ—Ö—Ä–∞–Ω–µ–Ω–∏–µ —É–∂–µ –≤—ã–ø–æ–ª–Ω—è–µ—Ç—Å—è...');
            return;
        }

        setIsSaving(true);
        setLoading(true);

        try {
            const currentId = formData.id || originalId;

            // ‚úÖ –§–æ—Ä–º–∏—Ä—É–µ–º –¥–∞–Ω–Ω—ã–µ –¥–ª—è –æ—Ç–ø—Ä–∞–≤–∫–∏
            const updateData = {
                equipment_type: formData.equipment_type.trim(),
                model: formData.model.trim(),
                status: formData.status,
                planned_hours: parseFloat(formData.planned_hours) || 0,
                malfunction: formData.malfunction?.trim() || '',
                mechanic_name: formData.mechanic_name?.trim() || ''
            };

            console.log('üì§ –û—Ç–ø—Ä–∞–≤–∫–∞ –Ω–∞ —Å–µ—Ä–≤–µ—Ä:', {
                id: currentId,
                ...updateData
            });

            // ‚úÖ –û—Ç–ø—Ä–∞–≤–ª—è–µ–º –∑–∞–ø—Ä–æ—Å
            const response = await api.put(`/equipment/${currentId}`, updateData);

            console.log('‚úÖ –û—Ç–≤–µ—Ç —Å–µ—Ä–≤–µ—Ä–∞:', response.data);

            // ‚úÖ –ü–æ–∫–∞–∑—ã–≤–∞–µ–º —É—Å–ø–µ—à–Ω–æ–µ —Å–æ–æ–±—â–µ–Ω–∏–µ
            toast.success('–î–∞–Ω–Ω—ã–µ —É—Å–ø–µ—à–Ω–æ —Å–æ—Ö—Ä–∞–Ω–µ–Ω—ã!', {
                autoClose: 2000,
                position: 'top-center'
            });

            // ‚úÖ –û–±–Ω–æ–≤–ª—è–µ–º –¥–∞–Ω–Ω—ã–µ –≤ —Ä–æ–¥–∏—Ç–µ–ª—å—Å–∫–æ–º –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–µ
            if (onSave) {
                await onSave();
            }

            // ‚úÖ –ó–∞–∫—Ä—ã–≤–∞–µ–º –º–æ–¥–∞–ª—å–Ω–æ–µ –æ–∫–Ω–æ
            setTimeout(() => {
                onClose();
            }, 500);

        } catch (error) {
            console.error('‚ùå –û—à–∏–±–∫–∞ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏—è:', error);
            console.error('‚ùå –î–µ—Ç–∞–ª–∏ –æ—à–∏–±–∫–∏:', {
                status: error.response?.status,
                statusText: error.response?.statusText,
                data: error.response?.data,
                message: error.message
            });

            // ‚úÖ –ü–æ–∫–∞–∑—ã–≤–∞–µ–º –ø–æ–Ω—è—Ç–Ω–æ–µ —Å–æ–æ–±—â–µ–Ω–∏–µ –æ–± –æ—à–∏–±–∫–µ
            const errorMessage = error.response?.data?.message
                || error.response?.data?.error
                || error.message
                || '–û—à–∏–±–∫–∞ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏—è –¥–∞–Ω–Ω—ã—Ö';

            toast.error(`–ù–µ —É–¥–∞–ª–æ—Å—å —Å–æ—Ö—Ä–∞–Ω–∏—Ç—å: ${errorMessage}`, {
                autoClose: 5000,
                position: 'top-center'
            });
        } finally {
            setLoading(false);
            setIsSaving(false);
        }
    };

    const handleChange = (e) => {
        const { name, value, type } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: type === 'number' ? parseFloat(value) || 0 : value
        }));
    };

    const handleBackdropClick = (e) => {
        if (e.target === e.currentTarget) {
            onClose();
        }
    };

    const getStatusText = (status) => {
        const statusMap = {
            'Down': '–ù–µ —Ä–∞–±–æ—Ç–∞–µ—Ç',
            'Ready': '–ì–æ—Ç–æ–≤–∞',
            'Standby': '–û–∂–∏–¥–∞–Ω–∏–µ',
            'Delay': '–ó–∞–¥–µ—Ä–∂–∫–∞',
            'Shiftchange': '–°–º–µ–Ω–∞'
        };
        return statusMap[status] || status;
    };

    const formatDateTime = (dateString) => {
        if (!dateString) return '-';
        try {
            return new Date(dateString).toLocaleString('ru-RU');
        } catch (error) {
            console.error('Error formatting date:', error);
            return dateString;
        }
    };

    const getActionText = (action) => {
        const actionMap = {
            'create': '–°–æ–∑–¥–∞–Ω–∏–µ',
            'update_status': '–ò–∑–º–µ–Ω–µ–Ω–∏–µ —Å—Ç–∞—Ç—É—Å–∞',
            'update_progress': '–û–±–Ω–æ–≤–ª–µ–Ω–∏–µ –ø—Ä–æ–≥—Ä–µ—Å—Å–∞',
            'update_mechanic_name': '–ù–∞–∑–Ω–∞—á–µ–Ω–∏–µ –º–µ—Ö–∞–Ω–∏–∫–∞',
            'update_type': '–ò–∑–º–µ–Ω–µ–Ω–∏–µ —Ç–∏–ø–∞',
            'update_model': '–ò–∑–º–µ–Ω–µ–Ω–∏–µ –º–æ–¥–µ–ª–∏',
            'update_planned_hours': '–ò–∑–º–µ–Ω–µ–Ω–∏–µ –ø–ª–∞–Ω–æ–≤–æ–≥–æ –≤—Ä–µ–º–µ–Ω–∏',
            'change_id': '–ò–∑–º–µ–Ω–µ–Ω–∏–µ ID',
            'delete': '–£–¥–∞–ª–µ–Ω–∏–µ'
        };
        return actionMap[action] || action;
    };

    const getHoursText = (hours) => {
        if (hours === 1) return '—á–∞—Å';
        if (hours >= 2 && hours <= 4) return '—á–∞—Å–∞';
        return '—á–∞—Å–æ–≤';
    };

    if (!isOpen || !equipment) return null;

    const canEdit = user && (user.role === 'admin' || user.role === 'dispatcher' || user.role === 'programmer');

    return (
        <div className="modal-backdrop" onClick={handleBackdropClick}>
            <div className="modal-content equipment-modal">
                <div className="modal-header">
                    <div>
                        <h2>–û–±–æ—Ä—É–¥–æ–≤–∞–Ω–∏–µ {originalId}</h2>
                        <p className="equipment-subtitle">
                            {getEquipmentTypeText(equipment.equipment_type)} {equipment.model}
                        </p>
                    </div>
                    <button className="close-button" onClick={onClose}>√ó</button>
                </div>

                <div className="modal-tabs">
                    <button
                        className={`tab ${activeTab === 'view' ? 'active' : ''}`}
                        onClick={() => setActiveTab('view')}
                    >
                        –ü—Ä–æ—Å–º–æ—Ç—Ä
                    </button>
                    {canEdit && (
                        <button
                            className={`tab ${activeTab === 'edit' ? 'active' : ''}`}
                            onClick={() => setActiveTab('edit')}
                        >
                            –†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ
                        </button>
                    )}
                    {canEdit && (
                        <button
                            className={`tab ${activeTab === 'history' ? 'active' : ''}`}
                            onClick={() => setActiveTab('history')}
                        >
                            –ò—Å—Ç–æ—Ä–∏—è
                        </button>
                    )}
                </div>

                <div className="modal-body">
                    {activeTab === 'view' && (
                        <div className="view-content">
                            <div className="info-grid">
                                <div className="info-item">
                                    <label>ID:</label>
                                    <span className="equipment-id">{equipment.id}</span>
                                </div>

                                <div className="info-item">
                                    <label>–¢–∏–ø:</label>
                                    <span>{getEquipmentTypeText(equipment.equipment_type)}</span>
                                </div>

                                <div className="info-item">
                                    <label>–ú–æ–¥–µ–ª—å:</label>
                                    <span>{equipment.model}</span>
                                </div>

                                <div className="info-item">
                                    <label>–°—Ç–∞—Ç—É—Å:</label>
                                    <span className={`status-badge ${equipment.status}`}>
                                        {getStatusText(equipment.status)}
                                    </span>
                                </div>

                                <div className="info-item">
                                    <label>–ü–ª–∞–Ω–æ–≤–æ–µ –≤—Ä–µ–º—è:</label>
                                    <span style={{
                                        color: equipment.planned_hours > 0 ? '#ffffff' : 'rgba(255, 255, 255, 0.4)',
                                        fontStyle: equipment.planned_hours > 0 ? 'normal' : 'italic'
                                    }}>
                                        {equipment.planned_hours > 0
                                            ? `${equipment.planned_hours} ${getHoursText(equipment.planned_hours)}`
                                            : '–ù–µ —É–∫–∞–∑–∞–Ω–æ'
                                        }
                                    </span>
                                </div>

                                <div className="info-item full-width">
                                    <label>–ù–µ–∏—Å–ø—Ä–∞–≤–Ω–æ—Å—Ç—å:</label>
                                    <span>{equipment.malfunction || '–ù–µ —É–∫–∞–∑–∞–Ω–∞'}</span>
                                </div>

                                <div className="info-item full-width">
                                    <label>–ú–µ—Ö–∞–Ω–∏–∫:</label>
                                    <span>{equipment.mechanic_name || '–ù–µ –Ω–∞–∑–Ω–∞—á–µ–Ω'}</span>
                                </div>
                            </div>
                        </div>
                    )}

                    {activeTab === 'edit' && canEdit && (
                        <form onSubmit={handleSubmit} className="edit-form">
                            <div className="form-grid">
                                <div className="form-group">
                                    <label>ID –æ–±–æ—Ä—É–¥–æ–≤–∞–Ω–∏—è</label>
                                    <div style={{ display: 'flex', gap: '10px' }}>
                                        <input
                                            type="text"
                                            name="id"
                                            value={formData.id}
                                            onChange={handleChange}
                                            disabled={loading}
                                            placeholder="–ù–∞–ø—Ä–∏–º–µ—Ä: EX001, LD002"
                                            style={{ flex: 1 }}
                                        />
                                        {formData.id !== originalId && (
                                            <button
                                                type="button"
                                                onClick={() => setShowIdChangeConfirm(true)}
                                                disabled={loading}
                                                style={{
                                                    background: '#ffc107',
                                                    color: '#000',
                                                    border: 'none',
                                                    padding: '8px 12px',
                                                    borderRadius: '4px',
                                                    cursor: 'pointer',
                                                    fontSize: '0.8rem'
                                                }}
                                            >
                                                –ò–∑–º–µ–Ω–∏—Ç—å ID
                                            </button>
                                        )}
                                    </div>
                                    {formData.id !== originalId && (
                                        <small style={{ color: '#ffc107', fontSize: '0.8rem' }}>
                                            ‚ö†Ô∏è ID –±—É–¥–µ—Ç –∏–∑–º–µ–Ω–µ–Ω. –ù–∞–∂–º–∏—Ç–µ "–ò–∑–º–µ–Ω–∏—Ç—å ID" –¥–ª—è –ø–æ–¥—Ç–≤–µ—Ä–∂–¥–µ–Ω–∏—è.
                                        </small>
                                    )}
                                </div>

                                <div className="form-group">
                                    <label>–¢–∏–ø</label>
                                    <select
                                        name="equipment_type"
                                        value={formData.equipment_type}
                                        onChange={handleChange}
                                        disabled={loading}
                                    >
                                        <option value="">–í—ã–±–µ—Ä–∏—Ç–µ —Ç–∏–ø</option>
                                        <option value="–≠–∫—Å–∫–∞–≤–∞—Ç–æ—Ä">–≠–∫—Å–∫–∞–≤–∞—Ç–æ—Ä</option>
                                        <option value="–ü–æ–≥—Ä—É–∑—á–∏–∫">–ü–æ–≥—Ä—É–∑—á–∏–∫</option>
                                        <option value="–ü–æ–ª–∏–≤–æ—á–Ω–∞—è –º–∞—à–∏–Ω–∞">–ü–æ–ª–∏–≤–æ—á–Ω–∞—è –º–∞—à–∏–Ω–∞</option>
                                        <option value="–ë—É–ª—å–¥–æ–∑–µ—Ä">–ë—É–ª—å–¥–æ–∑–µ—Ä</option>
                                        <option value="–ë—É—Ä–æ–≤–∞—è —É—Å—Ç–∞–Ω–æ–≤–∫–∞">–ë—É—Ä–æ–≤–∞—è —É—Å—Ç–∞–Ω–æ–≤–∫–∞</option>
                                        <option value="–ì—Ä–µ–π–¥–µ—Ä">–ì—Ä–µ–π–¥–µ—Ä</option>
                                        <option value="–°–∞–º–æ—Å–≤–∞–ª">–°–∞–º–æ—Å–≤–∞–ª</option>
                                        <option value="–¢–µ—Ö–Ω–∏–∫–∞">–î—Ä—É–≥–æ–µ</option>
                                    </select>
                                </div>

                                <div className="form-group">
                                    <label>–ú–æ–¥–µ–ª—å</label>
                                    <input
                                        type="text"
                                        name="model"
                                        value={formData.model}
                                        onChange={handleChange}
                                        disabled={loading}
                                        placeholder="–ù–∞–ø—Ä–∏–º–µ—Ä: CAT 320D, Volvo L120H"
                                    />
                                </div>

                                <div className="form-group">
                                    <label>–°—Ç–∞—Ç—É—Å</label>
                                    <select
                                        name="status"
                                        value={formData.status}
                                        onChange={handleChange}
                                        disabled={loading}
                                    >
                                        <option value="Ready">Ready</option>
                                        <option value="Down">Down</option>
                                        <option value="Standby">Standby</option>
                                        <option value="Delay">Delay</option>
                                        <option value="Shiftchange">Shiftchange</option>
                                    </select>
                                </div>

                                <div className="form-group">
                                    <label>–ü–ª–∞–Ω–æ–≤–æ–µ –≤—Ä–µ–º—è (—á–∞—Å—ã)</label>

                                    <div style={{
                                        display: 'flex',
                                        gap: '8px',
                                        marginBottom: '10px',
                                        flexWrap: 'wrap'
                                    }}>
                                        {[1, 2, 4, 6, 8, 12, 24, 48].map(hours => (
                                            <button
                                                key={hours}
                                                type="button"
                                                onClick={() => setFormData(prev => ({
                                                    ...prev,
                                                    planned_hours: hours
                                                }))}
                                                disabled={loading}
                                                style={{
                                                    background: formData.planned_hours === hours
                                                        ? 'rgba(79, 172, 254, 0.3)'
                                                        : 'rgba(255, 255, 255, 0.1)',
                                                    border: `1px solid ${formData.planned_hours === hours
                                                        ? '#4facfe'
                                                        : 'rgba(255, 255, 255, 0.2)'}`,
                                                    color: '#ffffff',
                                                    padding: '6px 12px',
                                                    borderRadius: '6px',
                                                    cursor: loading ? 'not-allowed' : 'pointer',
                                                    fontSize: '0.85rem',
                                                    transition: 'all 0.2s ease',
                                                    fontWeight: formData.planned_hours === hours ? '600' : '400'
                                                }}
                                            >
                                                {hours}—á
                                            </button>
                                        ))}
                                    </div>

                                    <input
                                        type="text"
                                        name="planned_hours"
                                        value={formData.planned_hours || ''}
                                        onChange={handlePlannedHoursChange}
                                        disabled={loading}
                                        placeholder="–ò–ª–∏ –≤–≤–µ–¥–∏—Ç–µ –≤—Ä—É—á–Ω—É—é: 1.5, 6.5, 8..."
                                        style={{
                                            fontFamily: 'monospace',
                                            fontSize: '1rem'
                                        }}
                                    />
                                    <small style={{
                                        color: 'rgba(255, 255, 255, 0.6)',
                                        fontSize: '0.8rem',
                                        display: 'block',
                                        marginTop: '5px'
                                    }}>
                                        üí° –í–≤–µ–¥–∏—Ç–µ –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ —á–∞—Å–æ–≤ (0-1000). –ú–æ–∂–Ω–æ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å –¥–µ—Å—è—Ç–∏—á–Ω—ã–µ: 1.5, 6, 8.5
                                    </small>
                                </div>

                                <div className="form-group full-width">
                                    <label>–ù–µ–∏—Å–ø—Ä–∞–≤–Ω–æ—Å—Ç—å</label>
                                    <textarea
                                        name="malfunction"
                                        value={formData.malfunction}
                                        onChange={handleChange}
                                        rows="3"
                                        placeholder="–û–ø–∏—Å–∞–Ω–∏–µ –Ω–µ–∏—Å–ø—Ä–∞–≤–Ω–æ—Å—Ç–∏..."
                                        disabled={loading}
                                    />
                                </div>

                                <div className="form-group full-width">
                                    <label>–ú–µ—Ö–∞–Ω–∏–∫</label>
                                    <input
                                        type="text"
                                        name="mechanic_name"
                                        value={formData.mechanic_name}
                                        onChange={handleChange}
                                        placeholder="–§–ò–û –º–µ—Ö–∞–Ω–∏–∫–∞"
                                        disabled={loading}
                                    />
                                </div>
                            </div>

                            <div className="form-actions">
                                <button
                                    type="button"
                                    className="cancel-button"
                                    onClick={onClose}
                                    disabled={loading}
                                >
                                    –û—Ç–º–µ–Ω–∞
                                </button>
                                <button
                                    type="submit"
                                    className="save-button"
                                    disabled={loading || isSaving}
                                >
                                    {loading || isSaving ? '–°–æ—Ö—Ä–∞–Ω–µ–Ω–∏–µ...' : '–°–æ—Ö—Ä–∞–Ω–∏—Ç—å'}
                                </button>
                            </div>
                        </form>
                    )}

                    {activeTab === 'history' && canEdit && (
                        <div className="history-content">
                            {historyLoading ? (
                                <div style={{
                                    display: 'flex',
                                    flexDirection: 'column',
                                    alignItems: 'center',
                                    justifyContent: 'center',
                                    padding: '40px',
                                    color: 'rgba(255, 255, 255, 0.7)'
                                }}>
                                    <div style={{
                                        width: '40px',
                                        height: '40px',
                                        border: '4px solid rgba(79, 172, 254, 0.2)',
                                        borderTop: '4px solid #4facfe',
                                        borderRadius: '50%',
                                        animation: 'spin 1s linear infinite',
                                        marginBottom: '15px'
                                    }}></div>
                                    <p>–ó–∞–≥—Ä—É–∑–∫–∞ –∏—Å—Ç–æ—Ä–∏–∏ –∏–∑–º–µ–Ω–µ–Ω–∏–π...</p>
                                </div>
                            ) : !history || history.length === 0 ? (
                                <p className="no-history" style={{
                                    textAlign: 'center',
                                    padding: '40px',
                                    color: 'rgba(255, 255, 255, 0.6)'
                                }}>
                                    {!history ? '–ò—Å—Ç–æ—Ä–∏—è –Ω–µ–¥–æ—Å—Ç—É–ø–Ω–∞' : '–ò—Å—Ç–æ—Ä–∏—è –∏–∑–º–µ–Ω–µ–Ω–∏–π –ø—É—Å—Ç–∞'}
                                </p>
                            ) : (
                                <div className="history-list">
                                    {history.map((item) => (
                                        <div key={item.id} className="history-item">
                                            <div className="history-header">
                                                <span className="history-action">
                                                    {getActionText(item.action)}
                                                </span>
                                                <span className="history-time">
                                                    {formatDateTime(item.timestamp)}
                                                </span>
                                            </div>
                                            <div className="history-user">
                                                –ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å: {item.full_name || item.username}
                                            </div>
                                            {item.old_value && item.new_value && (
                                                <div className="history-changes">
                                                    <span className="old-value">"{item.old_value}"</span>
                                                    <span className="arrow">‚Üí</span>
                                                    <span className="new-value">"{item.new_value}"</span>
                                                </div>
                                            )}
                                        </div>
                                    ))}
                                </div>
                            )}
                        </div>
                    )}
                </div>

                {showIdChangeConfirm && (
                    <div className="modal-backdrop" onClick={(e) => {
                        if (e.target === e.currentTarget) setShowIdChangeConfirm(false);
                    }}>
                        <div className="modal-content" style={{ maxWidth: '500px' }}>
                            <div className="modal-header">
                                <h3>–ò–∑–º–µ–Ω–µ–Ω–∏–µ ID –æ–±–æ—Ä—É–¥–æ–≤–∞–Ω–∏—è</h3>
                                <button
                                    className="close-button"
                                    onClick={() => setShowIdChangeConfirm(false)}
                                >
                                    √ó
                                </button>
                            </div>
                            <div style={{ padding: '20px' }}>
                                <p>–í—ã —É–≤–µ—Ä–µ–Ω—ã, —á—Ç–æ —Ö–æ—Ç–∏—Ç–µ –∏–∑–º–µ–Ω–∏—Ç—å ID –æ–±–æ—Ä—É–¥–æ–≤–∞–Ω–∏—è?</p>
                                <div style={{
                                    background: 'rgba(255, 193, 7, 0.1)',
                                    padding: '15px',
                                    borderRadius: '8px',
                                    margin: '15px 0'
                                }}>
                                    <strong>–°—Ç–∞—Ä—ã–π ID:</strong> {originalId}<br />
                                    <strong>–ù–æ–≤—ã–π ID:</strong> {formData.id}
                                </div>
                                <p style={{ fontSize: '0.9rem', color: 'rgba(255,255,255,0.7)' }}>
                                    ‚ö†Ô∏è –≠—Ç–æ –¥–µ–π—Å—Ç–≤–∏–µ –∏–∑–º–µ–Ω–∏—Ç ID –æ–±–æ—Ä—É–¥–æ–≤–∞–Ω–∏—è –≤–æ –≤—Å–µ–π —Å–∏—Å—Ç–µ–º–µ –∏ –∏—Å—Ç–æ—Ä–∏–∏.
                                </p>
                                <div style={{
                                    display: 'flex',
                                    gap: '10px',
                                    justifyContent: 'flex-end',
                                    marginTop: '20px'
                                }}>
                                    <button
                                        type="button"
                                        className="cancel-button"
                                        onClick={() => {
                                            setFormData(prev => ({ ...prev, id: originalId }));
                                            setShowIdChangeConfirm(false);
                                        }}
                                        disabled={loading}
                                    >
                                        –û—Ç–º–µ–Ω–∞
                                    </button>
                                    <button
                                        type="button"
                                        onClick={handleIdChange}
                                        disabled={loading}
                                        style={{
                                            background: '#ffc107',
                                            color: '#000',
                                            border: 'none',
                                            padding: '10px 20px',
                                            borderRadius: '6px',
                                            cursor: 'pointer',
                                            fontWeight: '600'
                                        }}
                                    >
                                        {loading ? '–ò–∑–º–µ–Ω–µ–Ω–∏–µ...' : '–ü–æ–¥—Ç–≤–µ—Ä–¥–∏—Ç—å –∏–∑–º–µ–Ω–µ–Ω–∏–µ ID'}
                                    </button>
                                </div>
                            </div>
                        </div>
                    </div>
                )}

                <style jsx>{`
                    @keyframes spin {
                        0% { transform: rotate(0deg); }
                        100% { transform: rotate(360deg); }
                    }
                `}</style>
            </div>
        </div>
    );
};

export default EquipmentTable;
