export const formatters = {
    equipmentId: (id) => id?.toUpperCase(),

    time: (timeString) => {
        if (!timeString) return '';
        // –û–±–µ—Å–ø–µ—á–∏–≤–∞–µ–º —Ñ–æ—Ä–º–∞—Ç HH:MM
        const parts = timeString.split(':');
        if (parts.length === 2) {
            const hours = parts[0].padStart(2, '0');
            const minutes = parts[1].padStart(2, '0');
            return `${hours}:${minutes}`;
        }
        return timeString;
    },

    status: (status) => {
        const statusMap = {
            'in_repair': '–í —Ä–µ–º–æ–Ω—Ç–µ',
            'ready': '–ì–æ—Ç–æ–≤–æ',
            'waiting': '–û–∂–∏–¥–∞–Ω–∏–µ',
            'scheduled': '–ó–∞–ø–ª–∞–Ω–∏—Ä–æ–≤–∞–Ω–æ'
        };
        return statusMap[status] || status;
    },

    priority: (priority) => {
        const priorityMap = {
            'low': '–ù–∏–∑–∫–∏–π',
            'normal': '–û–±—ã—á–Ω—ã–π',
            'medium': '–°—Ä–µ–¥–Ω–∏–π',
            'high': '–í—ã—Å–æ–∫–∏–π',
            'critical': '–ö—Ä–∏—Ç–∏—á–µ—Å–∫–∏–π'
        };
        return priorityMap[priority] || priority;
    },

    equipmentType: (type) => {
        const typeMap = {
            'excavator': '–≠–∫—Å–∫–∞–≤–∞—Ç–æ—Ä',
            'loader': '–ü–æ–≥—Ä—É–∑—á–∏–∫'
