// frontend/src/App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { AuthProvider, useAuth } from './contexts/AuthContext'; // ← ДОБАВИЛИ useAuth

// Импортируем существующие компоненты с правильными именами файлов
import Dashboard from './components/Dashboard'; // Dashboard.js существует
import Archive from './components/archive';     // archive.js (с маленькой буквы!)
import Login from './components/LoginModal';    // LoginModal.js (не Login!)

// Компоненты для обработки ошибок - нужно создать или использовать существующие
// import ErrorNotification from './components/ErrorNotification'; // ErrorNotification.css существует
import StatusCards from './components/StatusCards'; // StatusCards.js существует (можно использовать вместо ConnectionStatus)

import './App.css';

// ИСПРАВЛЕННЫЙ ProtectedRoute - использует useAuth вместо localStorage
const ProtectedRoute = ({ children, requiredRoles }) => {
    const { user, isAuthenticated, loading } = useAuth(); // ← ИСПОЛЬЗУЕМ КОНТЕКСТ

    // Пока идёт загрузка - показываем индикатор
    if (loading) {
        return (
            <div style={{
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                minHeight: '100vh'
            }}>
                Загрузка...
            </div>
        );
    }

    // Если не авторизован - редирект на login
    if (!isAuthenticated) {
        return <Navigate to="/login" replace />;
    }

    // Проверка ролей
    if (requiredRoles && !requiredRoles.includes(user?.role)) {
        return (
            <div style={{
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                justifyContent: 'center',
                minHeight: '100vh'
            }}>
                <h2>Доступ запрещен</h2>
                <p>У вас недостаточно прав для просмотра этой страницы</p>
                <p style={{ color: '#666' }}>Ваша роль: {user?.role}</p>
                <button onClick={() => window.history.back()}>Назад</button>
            </div>
        );
    }

    return children;
};

// Временный компонент ErrorNotification (если ErrorNotification.js не существует)
const ErrorNotificationComponent = () => {
    // Используем существующие стили из ErrorNotification.css
    return null; // Временно пустой
};

// Временный компонент ConnectionStatus на основе StatusCards
const ConnectionStatus = () => {
    return null; // Или можно использовать <StatusCards />
};

// WRAPPER для Login - делаем его работающим без props
const LoginWrapper = () => {
    const { login, error, loading, isAuthenticated } = useAuth();
    const [isOpen] = React.useState(true); // Модалка всегда открыта на /login

    // Если уже авторизован - редирект на dashboard
    if (isAuthenticated) {
        return <Navigate to="/dashboard" replace />;
    }

    const handleSuccess = () => {
        window.location.href = '/dashboard';
    };

    const handleClose = () => {
        // На странице логина закрытие ничего не делает
        // или можно сделать редирект куда-то
    };

    return (
        <Login 
            isOpen={isOpen}
            onClose={handleClose}
            onSuccess={handleSuccess}
        />
    );
};

function App() {
    return (
        <AuthProvider>
            <Router>
                <div className="App">
                    {/* Глобальные компоненты */}
                    <ErrorNotificationComponent />
                    <ConnectionStatus />

                    <Routes>
                        {/* Страница входа - используем LoginModal с wrapper */}
                        <Route path="/login" element={<LoginWrapper />} />

                        {/* Dashboard - только для admin и dispatcher */}
                        <Route path="/dashboard" element={
                            <ProtectedRoute requiredRoles={['admin', 'dispatcher']}>
                                <Dashboard />
                            </ProtectedRoute>
                        } />

                        {/* Archive - для admin, dispatcher и viewer */}
                        <Route path="/archive" element={
                            <ProtectedRoute requiredRoles={['admin', 'dispatcher', 'viewer']}>
                                <Archive />
                            </ProtectedRoute>
                        } />

                        {/* Главная страница - редирект на dashboard */}
                        <Route path="/" element={
                            <ProtectedRoute>
                                <Dashboard />
                            </ProtectedRoute>
                        } />

                        {/* Обработка несуществующих роутов */}
                        <Route path="*" element={<Navigate to="/" replace />} />
                    </Routes>
                </div>
            </Router>
        </AuthProvider>
    );
}

export default App;
