// frontend/src/contexts/AuthContext.js
import React, { createContext, useState, useContext, useEffect } from 'react';
import apiService, { api } from '../services/api';

const AuthContext = createContext(null);

export const useAuth = () => {
    const context = useContext(AuthContext);
    if (!context) {
        throw new Error('useAuth must be used within AuthProvider');
    }
    return context;
};

export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        checkAuth();
    }, []);

    const checkAuth = async () => {
        console.log('ðŸ” Checking authentication...');

        try {
            const token = localStorage.getItem('token');
            const savedUser = localStorage.getItem('user');

            if (token && savedUser) {
                const userData = JSON.parse(savedUser);
                setUser(userData);
                console.log('âœ… User restored:', userData);
            } else {
                console.log('â„¹ï¸ No saved authentication');
                setUser(null);
            }
        } catch (err) {
            console.error('âŒ Auth check error:', err);
            setUser(null);
        } finally {
            setLoading(false);
        }
    };

    const login = async (username, password) => {  // Ð˜Ð·Ð¼ÐµÐ½Ð¸Ð» email -> username
        console.log('Login attempt for:', username);
        setLoading(true);
        setError(null);

        try {
            const response = await api.post('/auth/login', { username, password });

            console.log('âœ… Login response:', response.data);

            // Ð¡Ð¾Ñ…Ñ€Ð°Ð½ÑÐµÐ¼ Ñ‚Ð¾ÐºÐµÐ½ Ð¸ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ
            if (response.data.token) {
                localStorage.setItem('token', response.data.token);
                localStorage.setItem('user', JSON.stringify(response.data.user));
                setUser(response.data.user);
                setError(null);
                console.log('âœ… Token saved successfully'); // âœ… Ð˜Ð¡ÐŸÐ ÐÐ’Ð›Ð•ÐÐž: Ð½Ðµ Ð»Ð¾Ð³Ð¸Ñ€ÑƒÐµÐ¼ ÑÐ°Ð¼ Ñ‚Ð¾ÐºÐµÐ½
                return { success: true };
            } else {
                throw new Error('Ð¢Ð¾ÐºÐµÐ½ Ð½Ðµ Ð¿Ð¾Ð»ÑƒÑ‡ÐµÐ½ Ð¾Ñ‚ ÑÐµÑ€Ð²ÐµÑ€Ð°');
            }
        } catch (err) {
            console.error('âŒ Login error:', err);
            const errorMessage = err.response?.data?.message || err.message || 'ÐžÑˆÐ¸Ð±ÐºÐ° Ð²Ñ…Ð¾Ð´Ð°';
            setError(errorMessage);
            return { success: false, error: errorMessage };
        } finally {
            setLoading(false);
        }
    };

    const logout = () => {
        console.log('ðŸ‘‹ Logout');
        setUser(null);
        setError(null);
        localStorage.removeItem('token');
        localStorage.removeItem('user');
    };

    const hasRole = (requiredRoles) => {
        if (!user) return false;
        if (!Array.isArray(requiredRoles)) {
            requiredRoles = [requiredRoles];
        }
        return requiredRoles.includes(user.role);
    };

    const isAuthenticated = () => {
        return !!user && !!localStorage.getItem('token');
    };

    const value = {
        user,
        loading,
        error,
        login,
        logout,
        checkAuth,
        hasRole,
        isAuthenticated: isAuthenticated(),
        setError
    };

    return (
        <AuthContext.Provider value={value}>
            {children}
        </AuthContext.Provider>
    );
};
