// frontend/src/contexts/AuthContext.js
import React, { createContext, useState, useContext, useEffect } from 'react';
import apiService, { api } from '../services/api';

const AuthContext = createContext(null);

export const useAuth = () => {
    const context = useContext(AuthContext);
    if (!context) {
        throw new Error('useAuth must be used within AuthProvider');
    }
    return context;
};

export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        checkAuth();
    }, []);

    const checkAuth = async () => {
        console.log('🔐 Checking authentication...');

        try {
            const token = localStorage.getItem('token');
            const savedUser = localStorage.getItem('user');

            if (token && savedUser) {
                const userData = JSON.parse(savedUser);
                setUser(userData);
                console.log('✅ User restored:', userData);
            } else {
                console.log('ℹ️ No saved authentication');
                setUser(null);
            }
        } catch (err) {
            console.error('❌ Auth check error:', err);
            setUser(null);
        } finally {
            setLoading(false);
        }
    };

    const login = async (email, password) => {
        console.log('🔑 Login attempt:', email);
        setLoading(true);
        setError(null);

        try {
            const result = await apiService.login(email, password);

            console.log('📊 Login result:', result);

            // ПРОВЕРЯЕМ что result существует!
            if (!result) {
                console.error('❌ Result is null/undefined');
                setError('Ошибка: нет ответа');
                setLoading(false);
                return { success: false, error: 'Нет ответа' };
            }

            if (result.success) {
                console.log('✅ Login successful');
                setUser(result.data.user);
                setError(null);
                setLoading(false);
                return { success: true };
            } else {
                console.log('❌ Login failed:', result.error);
                setError(result.error || 'Ошибка входа');
                setLoading(false);
                return { success: false, error: result.error };
            }
        } catch (err) {
            console.error('❌ Login exception:', err);
            const errorMessage = 'Ошибка соединения с сервером';
            setError(errorMessage);
            setLoading(false);
            return { success: false, error: errorMessage };
        }
    };

    const logout = () => {
        console.log('👋 Logout');
        setUser(null);
        setError(null);
        localStorage.removeItem('token');
        localStorage.removeItem('user');
    };

    const hasRole = (requiredRoles) => {
        if (!user) return false;
        if (!Array.isArray(requiredRoles)) {
            requiredRoles = [requiredRoles];
        }
        return requiredRoles.includes(user.role);
    };

    const isAuthenticated = () => {
        return !!user && !!localStorage.getItem('token');
    };

    const value = {
        user,
        loading,
        error,
        login,
        logout,
        checkAuth,
        hasRole,
        isAuthenticated: isAuthenticated(),
        setError
    };

    return (
        <AuthContext.Provider value={value}>
            {children}
        </AuthContext.Provider>
    );
};
