// frontend/src/components/LoginModal.js
import React, { useState } from 'react';
import { toast } from 'react-toastify';
import { useAuth } from '../contexts/AuthContext';
import { useNavigate } from 'react-router-dom'; // ‚Üê –î–û–ë–ê–í–ò–¢–¨
import './LoginModal.css';

const LoginModal = ({ isOpen, onClose, onSuccess }) => {
    const [formData, setFormData] = useState({
        username: '',
        password: ''
    });
    const [loading, setLoading] = useState(false);
    const { login } = useAuth();
    const navigate = useNavigate(); // ‚Üê –î–û–ë–ê–í–ò–¢–¨

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);

        try {
            console.log('üîë Attempting login with:', formData.username);

            const result = await login(formData.username, formData.password);

            console.log('üìä Login result:', result);

            if (result.success) {
                console.log('‚úÖ Login successful!');
                toast.success('–í—Ö–æ–¥ –≤—ã–ø–æ–ª–Ω–µ–Ω —É—Å–ø–µ—à–Ω–æ!');

                // –ó–∞–∫—Ä—ã–≤–∞–µ–º –º–æ–¥–∞–ª–∫—É
                if (onSuccess) {
                    onSuccess();
                }

                // –î–∞—ë–º –≤—Ä–µ–º—è –Ω–∞ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏–µ –≤ localStorage
                setTimeout(() => {
                    // –†–µ–¥–∏—Ä–µ–∫—Ç –Ω–∞ dashboard
                    navigate('/dashboard');
                    // –ò–ª–∏ –º–æ–∂–Ω–æ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å:
                    // window.location.href = '/dashboard';
                }, 100);
            } else {
                console.error('‚ùå Login failed:', result.error);
                toast.error(result.error || '–û—à–∏–±–∫–∞ –≤—Ö–æ–¥–∞');
            }
        } catch (error) {
            console.error('‚ùå Login exception:', error);
            toast.error(error.message || '–û—à–∏–±–∫–∞ –≤—Ö–æ–¥–∞');
        } finally {
            setLoading(false);
        }
    };

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    const handleBackdropClick = (e) => {
        if (e.target === e.currentTarget) {
            onClose();
        }
    };

    const handleAccountClick = (username) => {
        setFormData(prev => ({
            ...prev,
            username: username
        }));
        // –§–æ–∫—É—Å–∏—Ä—É–µ–º—Å—è –Ω–∞ –ø–æ–ª–µ –ø–∞—Ä–æ–ª—è
        setTimeout(() => {
            const passwordField = document.getElementById('password');
            if (passwordField) passwordField.focus();
        }, 100);
    };

    if (!isOpen) return null;

    return (
        <div className="modal-backdrop" onClick={handleBackdropClick}>
            <div className="modal-content">
                <div className="modal-header">
                    <h2>–í—Ö–æ–¥ –≤ —Å–∏—Å—Ç–µ–º—É</h2>
                    <button className="close-button" onClick={onClose}>
                        √ó
                    </button>
                </div>

                <form onSubmit={handleSubmit} className="login-form">
                    <div className="form-group">
                        <label htmlFor="username">–ò–º—è –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è</label>
                        <input
                            type="text"
                            id="username"
                            name="username"
                            value={formData.username}
                            onChange={handleChange}
                            required
                            placeholder="–í–≤–µ–¥–∏—Ç–µ email"
                            disabled={loading}
                        />
                    </div>

                    <div className="form-group">
                        <label htmlFor="password">–ü–∞—Ä–æ–ª—å</label>
                        <input
                            type="password"
                            id="password"
                            name="password"
                            value={formData.password}
                            onChange={handleChange}
                            required
                            placeholder="–í–≤–µ–¥–∏—Ç–µ –ø–∞—Ä–æ–ª—å"
                            disabled={loading}
                        />
                    </div>

                    <div className="form-actions">
                        <button
                            type="submit"
                            className="submit-button"
                            disabled={loading}
                        >
                            {loading ? (
                                <>
                                    <div className="button-spinner"></div>
                                    –í—Ö–æ–¥...
                                </>
                            ) : (
                                '–í–æ–π—Ç–∏'
                            )}
                        </button>
                    </div>
                </form>

                <div className="login-info">
                    <h3>–î–æ—Å—Ç—É–ø–Ω—ã–µ —É—á–µ—Ç–Ω—ã–µ –∑–∞–ø–∏—Å–∏:</h3>
                    <div className="test-accounts">
                        <div
                            className="account-info"
                            onClick={() => handleAccountClick('timur.abitov@kazminerals.com')}
                        >
                            <strong>–ê–¥–º–∏–Ω–∏—Å—Ç—Ä–∞—Ç–æ—Ä</strong>
                            <div className="login-label">Email:</div>
                            <div className="login-value">timur.abitov@kazminerals.com</div>
                            <div className="password-hint">–ù–∞–∂–º–∏—Ç–µ –¥–ª—è –∞–≤—Ç–æ–∑–∞–ø–æ–ª–Ω–µ–Ω–∏—è –ª–æ–≥–∏–Ω–∞</div>
                        </div>
                        <div
                            className="account-info"
                            onClick={() => handleAccountClick('kassymzhan.nuraliyev@kazminerals.com')}
                        >
                            <strong>–ê–¥–º–∏–Ω–∏—Å—Ç—Ä–∞—Ç–æ—Ä</strong>
                            <div className="login-label">Email:</div>
                            <div className="login-value">kassymzhan.nuraliyev@kazminerals.com</div>
                            <div className="password-hint">–ù–∞–∂–º–∏—Ç–µ –¥–ª—è –∞–≤—Ç–æ–∑–∞–ø–æ–ª–Ω–µ–Ω–∏—è –ª–æ–≥–∏–Ω–∞</div>
                        </div>
                        <div
                            className="account-info"
                            onClick={() => handleAccountClick('ualikhan.belgibay@kazminerals.com')}
                        >
                            <strong>–î–∏—Å–ø–µ—Ç—á–µ—Ä</strong>
                            <div className="login-label">Email:</div>
                            <div className="login-value">ualikhan.belgibay@kazminerals.com</div>
                            <div className="password-hint">–ù–∞–∂–º–∏—Ç–µ –¥–ª—è –∞–≤—Ç–æ–∑–∞–ø–æ–ª–Ω–µ–Ω–∏—è –ª–æ–≥–∏–Ω–∞</div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default LoginModal;
