// frontend/src/contexts/AuthContext.js
import React, { createContext, useState, useContext, useEffect } from 'react';
import apiService from '../services/api';

const AuthContext = createContext(null);

export const useAuth = () => {
    const context = useContext(AuthContext);
    if (!context) {
        throw new Error('useAuth must be used within AuthProvider');
    }
    return context;
};

export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // ÐŸÑ€Ð¾Ð²ÐµÑ€ÐºÐ° ÑÐ¾Ñ…Ñ€Ð°Ð½ÐµÐ½Ð½Ð¾Ð¹ Ð°Ð²Ñ‚Ð¾Ñ€Ð¸Ð·Ð°Ñ†Ð¸Ð¸ Ð¿Ñ€Ð¸ Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÐµ
    useEffect(() => {
        checkAuth();
    }, []);

    // frontend/src/contexts/AuthContext.js

    const checkAuth = async () => {
        console.log('ðŸ” Checking authentication...');

        try {
            const token = localStorage.getItem('token');
            const savedUser = localStorage.getItem('user');

            console.log('ðŸ“¦ Saved data:', {
                hasToken: !!token,
                hasUser: !!savedUser,
                token: token ? `${token.substring(0, 20)}...` : 'none'
            });

            if (token && savedUser) {
                console.log('âœ… Found saved token and user');

                // ÐŸÑ€Ð¾Ð²ÐµÑ€ÑÐµÐ¼ Ð²Ð°Ð»Ð¸Ð´Ð½Ð¾ÑÑ‚ÑŒ Ñ‚Ð¾ÐºÐµÐ½Ð° Ñ‡ÐµÑ€ÐµÐ· /api/auth/verify
                try {
                    const response = await api.get('/auth/verify');

                    if (response.status === 200) {
                        const userData = JSON.parse(savedUser);
                        setUser(userData);
                        console.log('âœ… User restored:', userData);
                    } else {
                        throw new Error('Token validation failed');
                    }
                } catch (verifyError) {
                    console.log('âŒ Token invalid, clearing...', verifyError.message);
                    localStorage.removeItem('token');
                    localStorage.removeItem('user');
                    setUser(null);
                }
            } else {
                console.log('â„¹ï¸ No saved authentication found');
                setUser(null);
            }
        } catch (err) {
            console.error('âŒ Auth check error:', err);
            setError('ÐžÑˆÐ¸Ð±ÐºÐ° Ð¿Ñ€Ð¾Ð²ÐµÑ€ÐºÐ¸ Ð°Ð²Ñ‚Ð¾Ñ€Ð¸Ð·Ð°Ñ†Ð¸Ð¸');
            setUser(null);
        } finally {
            setLoading(false);
        }
    };

    const login = async (email, password) => {
        console.log('Login attempt for:', email);
        setLoading(true);
        setError(null);

        try {
            const result = await apiService.login(email, password);

            if (result.success) {
                console.log('Login successful:', result.data);
                setUser(result.data.user);
                setError(null);
                return { success: true };
            } else {
                console.log('Login failed:', result.error);
                setError(result.error || 'ÐžÑˆÐ¸Ð±ÐºÐ° Ð²Ñ…Ð¾Ð´Ð°');
                return { success: false, error: result.error };
            }
        } catch (err) {
            console.error('Login error:', err);
            const errorMessage = err.response?.data?.message || 'ÐžÑˆÐ¸Ð±ÐºÐ° ÑÐ¾ÐµÐ´Ð¸Ð½ÐµÐ½Ð¸Ñ Ñ ÑÐµÑ€Ð²ÐµÑ€Ð¾Ð¼';
            setError(errorMessage);
            return { success: false, error: errorMessage };
        } finally {
            setLoading(false);
        }
    };

    const logout = () => {
        console.log('Logging out...');
        setUser(null);
        setError(null);
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        // ÐÐµ Ð´ÐµÐ»Ð°ÐµÐ¼ Ñ€ÐµÐ´Ð¸Ñ€ÐµÐºÑ‚ Ð·Ð´ÐµÑÑŒ, Ð¿ÑƒÑÑ‚ÑŒ ÐºÐ¾Ð¼Ð¿Ð¾Ð½ÐµÐ½Ñ‚ ÑÐ°Ð¼ Ñ€ÐµÑˆÐ°ÐµÑ‚
    };

    const hasRole = (requiredRoles) => {
        if (!user) return false;
        if (!Array.isArray(requiredRoles)) {
            requiredRoles = [requiredRoles];
        }
        return requiredRoles.includes(user.role);
    };

    const isAuthenticated = () => {
        return !!user && !!localStorage.getItem('token');
    };

    const value = {
        user,
        loading,
        error,
        login,
        logout,
        checkAuth,
        hasRole,
        isAuthenticated: isAuthenticated(),
        setError
    };

    return (
        <AuthContext.Provider value={value}>
            {children}
        </AuthContext.Provider>
    );
};
