import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import api from '../services/api';

const Archive = () => {
    const { user } = useAuth();
    const [archives, setArchives] = useState([]);
    const [loading, setLoading] = useState(true);
    const [stats, setStats] = useState({
        total_archived: 0,
        launched: 0,
        completed: 0,
        cancelled: 0
    });

    useEffect(() => {
        if (user && (user.role === 'admin' || user.role === 'dispatcher')) {
            fetchArchives();
            fetchStats();
        }
    }, [user]);

    const fetchArchives = async () => {
        try {
            setLoading(true);
            console.log('Загрузка архивных данных...');
            const response = await api.get('/archive');
            console.log('Получены архивные данные:', response.data);

            setArchives(response.data.archives || []);
        } catch (error) {
            console.error('Ошибка загрузки архива:', error);
            setArchives([]);
        } finally {
            setLoading(false);
        }
    };

    const fetchStats = async () => {
        try {
            console.log('Загрузка статистики архива...');
            const response = await api.get('/archive/stats');
            console.log('Получена статистика:', response.data);

            setStats(response.data.summary || {
                total_archived: 0,
                launched: 0,
                completed: 0,
                cancelled: 0
            });
        } catch (error) {
            console.error('Ошибка загрузки статистики:', error);
        }
    };

    const formatDateTime = (dateString) => {
        if (!dateString) return '-';
        return new Date(dateString).toLocaleString('ru-RU');
    };

    const getEquipmentTypeText = (type) => {
        return type === 'excavator' ? 'Экскаватор' : 'Погрузчик';
    };

    // Проверка прав доступа
    if (!user || (user.role !== 'admin' && user.role !== 'dispatcher')) {
        return (
            <div style={{
                textAlign: 'center',
                padding: '50px',
                color: 'rgba(255,255,255,0.7)'
            }}>
                <h3>Доступ запрещен</h3>
                <p>Только администраторы и диспетчеры могут просматривать архив</p>
            </div>
        );
    }

    return (
        <div style={{ padding: '20px 0' }}>
            {/* Заголовок */}
            <div style={{ marginBottom: '30px' }}>
                <h2 style={{
                    fontSize: '1.8rem',
                    background: 'linear-gradient(135deg, #4facfe 0%, #00f2fe 100%)',
                    backgroundClip: 'text',
                    WebkitBackgroundClip: 'text',
                    WebkitTextFillColor: 'transparent',
                    marginBottom: '10px'
                }}>
                    Архив Техники
                </h2>
                <p style={{ color: 'rgba(255,255,255,0.7)' }}>
                    История запущенного и завершенного оборудования
                </p>
            </div>

            {/* Статистика */}
            <div style={{
                display: 'grid',
                gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
                gap: '15px',
                marginBottom: '30px'
            }}>
                <div style={{
                    background: 'rgba(255, 255, 255, 0.05)',
                    padding: '20px',
                    borderRadius: '12px',
                    border: '1px solid rgba(255, 255, 255, 0.1)',
                    textAlign: 'center'
                }}>
                    <div style={{ fontSize: '2rem', marginBottom: '5px' }}>🚀</div>
                    <div style={{ fontSize: '2rem', fontWeight: 'bold', color: '#4facfe' }}>
                        {stats.launched || 0}
                    </div>
                    <div style={{ fontSize: '0.9rem', color: 'rgba(255,255,255,0.7)' }}>
                        Запущено
                    </div>
                </div>

                <div style={{
                    background: 'rgba(255, 255, 255, 0.05)',
                    padding: '20px',
                    borderRadius: '12px',
                    border: '1px solid rgba(255, 255, 255, 0.1)',
                    textAlign: 'center'
                }}>
                    <div style={{ fontSize: '2rem', marginBottom: '5px' }}>✅</div>
                    <div style={{ fontSize: '2rem', fontWeight: 'bold', color: '#28a745' }}>
                        {stats.completed || 0}
                    </div>
                    <div style={{ fontSize: '0.9rem', color: 'rgba(255,255,255,0.7)' }}>
                        Завершено
                    </div>
                </div>

                <div style={{
                    background: 'rgba(255, 255, 255, 0.05)',
                    padding: '20px',
                    borderRadius: '12px',
                    border: '1px solid rgba(255, 255, 255, 0.1)',
                    textAlign: 'center'
                }}>
                    <div style={{ fontSize: '2rem', marginBottom: '5px' }}>📁</div>
                    <div style={{ fontSize: '2rem', fontWeight: 'bold', color: '#6f42c1' }}>
                        {stats.total_archived || 0}
                    </div>
                    <div style={{ fontSize: '0.9rem', color: 'rgba(255,255,255,0.7)' }}>
                        Всего в архиве
                    </div>
                </div>
            </div>

            {/* Содержимое */}
            {loading ? (
                <div style={{
                    textAlign: 'center',
                    padding: '40px',
                    color: 'rgba(255,255,255,0.7)'
                }}>
                    <div style={{
                        width: '40px',
                        height: '40px',
                        border: '4px solid rgba(255, 255, 255, 0.1)',
                        borderLeft: '4px solid #4facfe',
                        borderRadius: '50%',
                        animation: 'spin 1s linear infinite',
                        margin: '0 auto 20px'
                    }}></div>
                    <p>Загрузка архивных данных...</p>
                </div>
            ) : archives.length === 0 ? (
                <div style={{
                    textAlign: 'center',
                    padding: '40px',
                    background: 'rgba(255, 255, 255, 0.03)',
                    borderRadius: '12px',
                    border: '1px solid rgba(255, 255, 255, 0.1)',
                    color: 'rgba(255,255,255,0.7)'
                }}>
                    <h3 style={{ marginBottom: '10px', color: 'rgba(255,255,255,0.8)' }}>
                        Архивные записи не найдены
                    </h3>
                    <p>Архив пока пуст. Запустите технику в работу через главное табло.</p>
                    <button
                        onClick={() => window.location.href = '/'}
                        style={{
                            background: 'linear-gradient(135deg, #4facfe, #00f2fe)',
                            border: 'none',
                            padding: '10px 20px',
                            borderRadius: '8px',
                            color: 'white',
                            cursor: 'pointer',
                            marginTop: '15px'
                        }}
                    >
                        Перейти к табло
                    </button>
                </div>
            ) : (
                <div style={{
                    background: 'rgba(255, 255, 255, 0.03)',
                    borderRadius: '12px',
                    border: '1px solid rgba(255, 255, 255, 0.1)',
                    overflow: 'hidden'
                }}>
                    <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                        <thead>
                            <tr style={{ background: 'rgba(255, 255, 255, 0.1)' }}>
                                <th style={{ padding: '15px', textAlign: 'left', fontWeight: '600' }}>ID</th>
                                <th style={{ padding: '15px', textAlign: 'left', fontWeight: '600' }}>Тип/Модель</th>
                                <th style={{ padding: '15px', textAlign: 'left', fontWeight: '600' }}>Механик</th>
                                <th style={{ padding: '15px', textAlign: 'left', fontWeight: '600' }}>Прогресс</th>
                                <th style={{ padding: '15px', textAlign: 'left', fontWeight: '600' }}>Дата архивирования</th>
                            </tr>
                        </thead>
                        <tbody>
                            {archives.map((item, index) => (
                                <tr key={item.archive_id || index} style={{
                                    borderBottom: '1px solid rgba(255, 255, 255, 0.05)'
                                }}>
                                    <td style={{ padding: '15px' }}>
                                        <span style={{
                                            fontWeight: 'bold',
                                            color: '#ffd700'
                                        }}>
                                            {item.id}
                                        </span>
                                    </td>
                                    <td style={{ padding: '15px' }}>
                                        <div>
                                            <div style={{ fontWeight: '600', marginBottom: '4px' }}>
                                                {getEquipmentTypeText(item.type)}
                                            </div>
                                            <div style={{
                                                fontSize: '0.8rem',
                                                color: 'rgba(255,255,255,0.7)'
                                            }}>
                                                {item.model}
                                            </div>
                                        </div>
                                    </td>
                                    <td style={{ padding: '15px' }}>
                                        {item.mechanic_name || '-'}
                                    </td>
                                    <td style={{ padding: '15px' }}>
                                        <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
                                            <div style={{
                                                width: '50px',
                                                height: '6px',
                                                background: 'rgba(255, 255, 255, 0.1)',
                                                borderRadius: '3px',
                                                overflow: 'hidden'
                                            }}>
                                                <div style={{
                                                    height: '100%',
                                                    width: `${item.progress || 0}%`,
                                                    background: 'linear-gradient(90deg, #28a745, #20c997)',
                                                    borderRadius: '3px'
                                                }}></div>
                                            </div>
                                            <span>{item.progress || 0}%</span>
                                        </div>
                                    </td>
                                    <td style={{
                                        padding: '15px',
                                        fontSize: '0.85rem',
                                        fontFamily: 'monospace',
                                        color: 'rgba(255,255,255,0.8)'
                                    }}>
                                        {formatDateTime(item.completed_date)}
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            )}

            <style>{`
                @keyframes spin {
                    0% { transform: rotate(0deg); }
                    100% { transform: rotate(360deg); }
                }
            `}</style>
        </div>
    );
};

export default archive;
