                    console.error('âŒ Ð”Ð°Ð½Ð½Ñ‹Ðµ Ð¾Ð±Ð¾Ñ€ÑƒÐ´Ð¾Ð²Ð°Ð½Ð¸Ñ Ð½Ðµ ÑÐ²Ð»ÑÑŽÑ‚ÑÑ Ð¼Ð°ÑÑÐ¸Ð²Ð¾Ð¼:', equipmentData);
