const ProtectedRoute = ({ children, requiredRoles }) => {
    const { user, isAuthenticated, loading } = useAuth();

    console.log('üõ°Ô∏è ProtectedRoute:', {
        isAuthenticated,
        loading,
        userRole: user?.role,
        requiredRoles
