function AppRoutes() {
    const { isAuthenticated } = useAuth();

    console.log('üó∫Ô∏è AppRoutes render, isAuthenticated:', isAuthenticated);

    return (
        <Routes>
            {/* –°—Ç—Ä–∞–Ω–∏—Ü–∞ –≤—Ö–æ–¥–∞ */}
            <Route path="/login" element={
                isAuthenticated ? <Navigate to="/dashboard" replace /> : <LoginPage />
            } />

            {/* Dashboard - –¥–ª—è admin –∏ dispatcher */}
            <Route path="/dashboard" element={
                <ProtectedRoute requiredRoles={['admin', 'dispatcher']}>
                    <Dashboard />
                </ProtectedRoute>
            } />

            {/* Archive - –¥–ª—è admin, dispatcher –∏ viewer */}
            <Route path="/archive" element={
                <ProtectedRoute requiredRoles={['admin', 'dispatcher', 'viewer']}>
                    <Archive />
                </ProtectedRoute>
            } />

            {/* –ì–ª–∞–≤–Ω–∞—è —Å—Ç—Ä–∞–Ω–∏—Ü–∞ */}
            <Route path="/" element={
                <Navigate to="/dashboard" replace />
            } />

            {/* 404 */}
            <Route path="*" element={<Navigate to="/" replace />} />
        </Routes>
    );
}

function App() {
    console.log('üöÄ App component render');

    return (
        <AuthProvider>
            <Router>
                <div className="App">
                    <AppRoutes />
                </div>
            </Router>
        </AuthProvider>
    );
}

export default App;
