// frontend/src/contexts/AuthContext.js
import React, { createContext, useState, useContext, useEffect } from 'react';
import apiService, { api } from '../services/api'; // ‚Üê –í–ê–ñ–ù–û: –∏–º–ø–æ—Ä—Ç–∏—Ä—É–µ–º –æ–±–∞!

const AuthContext = createContext(null);

export const useAuth = () => {
    const context = useContext(AuthContext);
    if (!context) {
        throw new Error('useAuth must be used within AuthProvider');
    }
    return context;
};

export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        checkAuth();
    }, []);

    const checkAuth = async () => {
        console.log('%cüîê AUTH CHECK', 'color: #9C27B0; font-size: 14px; font-weight: bold;');

        try {
            const token = localStorage.getItem('token');
            const savedUser = localStorage.getItem('user');

            console.log('Token exists:', !!token);
            console.log('User exists:', !!savedUser);

            if (token && savedUser) {
                console.log('‚úÖ Found saved credentials');

                // –í–ê–†–ò–ê–ù–¢ 1: –ï—Å–ª–∏ —É –≤–∞—Å –µ—Å—Ç—å endpoint /auth/verify
                // –†–∞—Å–∫–æ–º–º–µ–Ω—Ç–∏—Ä—É–π—Ç–µ –µ—Å–ª–∏ endpoint —Å—É—â–µ—Å—Ç–≤—É–µ—Ç:
                /*
                try {
                    const response = await api.get('/auth/verify');
                    
                    if (response.status === 200) {
                        const userData = JSON.parse(savedUser);
                        setUser(userData);
                        console.log('‚úÖ Token verified, user restored:', userData);
                    }
                } catch (verifyError) {
                    console.log('‚ùå Token verification failed:', verifyError.message);
                    localStorage.removeItem('token');
                    localStorage.removeItem('user');
                    setUser(null);
                }
                */

                // –í–ê–†–ò–ê–ù–¢ 2: –ï—Å–ª–∏ –ù–ï–¢ endpoint /auth/verify (–≤—Ä–µ–º–µ–Ω–Ω–æ–µ —Ä–µ—à–µ–Ω–∏–µ)
                // –ü—Ä–æ—Å—Ç–æ –≤–æ—Å—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è –∏–∑ localStorage
                const userData = JSON.parse(savedUser);
                setUser(userData);
                console.log('‚úÖ User restored from localStorage:', userData);

            } else {
                console.log('‚ÑπÔ∏è No saved authentication');
                setUser(null);
            }
        } catch (err) {
            console.error('‚ùå Auth check error:', err);
            setError('–û—à–∏–±–∫–∞ –ø—Ä–æ–≤–µ—Ä–∫–∏ –∞–≤—Ç–æ—Ä–∏–∑–∞—Ü–∏–∏');
            setUser(null);
        } finally {
            setLoading(false);
            console.log('‚úÖ Auth check complete');
        }
    };

    const login = async (email, password) => {
        console.log('%cüîë LOGIN PROCESS START', 'color: #2196F3; font-size: 14px; font-weight: bold;');
        console.log('Credentials:', { email, passwordLength: password.length });

        setLoading(true);
        setError(null);

        try {
            const result = await apiService.login(email, password);

            if (result.success) {
                console.log('%c‚úÖ LOGIN SUCCESSFUL', 'color: #4CAF50; font-size: 14px; font-weight: bold;');
                console.log('User data:', result.data.user);

                setUser(result.data.user);
                setError(null);

                return { success: true, user: result.data.user };
            } else {
                console.log('%c‚ùå LOGIN FAILED', 'color: #f44336; font-size: 14px; font-weight: bold;');
                console.log('Error:', result.error);

                setError(result.error || '–û—à–∏–±–∫–∞ –≤—Ö–æ–¥–∞');
                return { success: false, error: result.error };
            }
        } catch (err) {
            console.error('%c‚ùå LOGIN EXCEPTION', 'color: #f44336; font-size: 14px; font-weight: bold;');
            console.error('Exception:', err);

            const errorMessage = err.response?.data?.message || '–û—à–∏–±–∫–∞ —Å–æ–µ–¥–∏–Ω–µ–Ω–∏—è —Å —Å–µ—Ä–≤–µ—Ä–æ–º';
            setError(errorMessage);
            return { success: false, error: errorMessage };
        } finally {
            setLoading(false);
        }
    };

    const logout = () => {
        console.log('%cüëã LOGOUT', 'color: #FF9800; font-weight: bold;');
        setUser(null);
        setError(null);
        localStorage.removeItem('token');
        localStorage.removeItem('user');
    };

    const hasRole = (requiredRoles) => {
        if (!user) return false;
        if (!Array.isArray(requiredRoles)) {
            requiredRoles = [requiredRoles];
        }
        return requiredRoles.includes(user.role);
    };

    const isAuthenticated = () => {
        const authenticated = !!user && !!localStorage.getItem('token');
        console.log('isAuthenticated:', authenticated, { user: !!user, token: !!localStorage.getItem('token') });
        return authenticated;
    };

    const value = {
        user,
        loading,
        error,
        login,
        logout,
        checkAuth,
        hasRole,
        isAuthenticated: isAuthenticated(),
        setError
    };

    // –ü–æ–∫–∞–∑—ã–≤–∞–µ–º –∏–Ω–¥–∏–∫–∞—Ç–æ—Ä –∑–∞–≥—Ä—É–∑–∫–∏ –ø—Ä–∏ –ø–µ—Ä–≤–æ–π –ø—Ä–æ–≤–µ—Ä–∫–µ
    if (loading) {
        return (
            <div style={{
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                justifyContent: 'center',
                minHeight: '100vh',
                fontSize: '18px',
                gap: '20px'
            }}>
                <div style={{ fontSize: '48px' }}>‚è≥</div>
                <div>–ü—Ä–æ–≤–µ—Ä–∫–∞ –∞–≤—Ç–æ—Ä–∏–∑–∞—Ü–∏–∏...</div>
            </div>
        );
    }

    return (
        <AuthContext.Provider value={value}>
            {children}
        </AuthContext.Provider>
    );
};
