// –ü–æ–ª—É—á–∞–µ–º URL –∏–∑ –ø–µ—Ä–µ–º–µ–Ω–Ω–æ–π –æ–∫—Ä—É–∂–µ–Ω–∏—è –∏–ª–∏ –∏—Å–ø–æ–ª—å–∑—É–µ–º –¥–µ—Ñ–æ–ª—Ç–Ω—ã–π
const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5001/api';

console.log('%cüåê API Configuration', 'color: #4CAF50; font-size: 14px; font-weight: bold;');
console.log('API URL:', API_URL);
console.log('Environment:', process.env.NODE_ENV);

// –°–æ–∑–¥–∞–µ–º —ç–∫–∑–µ–º–ø–ª—è—Ä axios
const api = axios.create({
    baseURL: API_URL,
    timeout: 15000,
    headers: {
        'Content-Type': 'application/json',
    },
});

// –ò–Ω—Ç–µ—Ä—Ü–µ–ø—Ç–æ—Ä –¥–ª—è –¥–æ–±–∞–≤–ª–µ–Ω–∏—è —Ç–æ–∫–µ–Ω–∞ –∫ –∑–∞–ø—Ä–æ—Å–∞–º
api.interceptors.request.use(
    (config) => {
        const token = localStorage.getItem('token');

        console.log('%cüì§ API REQUEST', 'color: #2196F3; font-weight: bold;');
        console.log('Method:', config.method?.toUpperCase());
        console.log('URL:', `${config.baseURL}${config.url}`);
        console.log('Headers:', config.headers);
        console.log('Data:', config.data);

        if (token) {
            config.headers.Authorization = `Bearer ${token}`;
            console.log('‚úÖ Token added:', token.substring(0, 20) + '...');
        } else {
            console.log('‚ÑπÔ∏è No token available');
        }

        return config;
    },
    (error) => {
        console.error('%c‚ùå REQUEST ERROR', 'color: #f44336; font-weight: bold;', error);
        return Promise.reject(error);
    }
);

// –ò–Ω—Ç–µ—Ä—Ü–µ–ø—Ç–æ—Ä –¥–ª—è –æ–±—Ä–∞–±–æ—Ç–∫–∏ –æ—Ç–≤–µ—Ç–æ–≤
api.interceptors.response.use(
    (response) => {
        console.log('%cüì• API RESPONSE', 'color: #4CAF50; font-weight: bold;');
        console.log('Status:', response.status);
        console.log('URL:', response.config.url);
        console.log('Data:', response.data);
        return response;
    },
    (error) => {
        console.log('%c‚ùå API ERROR', 'color: #f44336; font-weight: bold;');

        if (error.response) {
            // –°–µ—Ä–≤–µ—Ä –æ—Ç–≤–µ—Ç–∏–ª —Å –∫–æ–¥–æ–º –æ—à–∏–±–∫–∏
            const { status, data, config } = error.response;

            console.log('Status Code:', status);
            console.log('URL:', config.url);
            console.log('Error Data:', data);
            console.log('Full Response:', error.response);

            switch (status) {
                case 400:
                    console.log('‚ùå Bad Request - –ø—Ä–æ–≤–µ—Ä—å—Ç–µ –¥–∞–Ω–Ω—ã–µ –∑–∞–ø—Ä–æ—Å–∞');
                    break;
                case 401:
                    console.log('‚ùå Unauthorized - –Ω–µ–≤–µ—Ä–Ω—ã–µ —É—á–µ—Ç–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ –∏–ª–∏ —Ç–æ–∫–µ–Ω');
                    localStorage.removeItem('token');
                    localStorage.removeItem('user');

                    if (!window.location.pathname.includes('/login')) {
                        console.log('Redirecting to /login...');
                        window.location.href = '/login';
                    }
                    break;
                case 403:
                    console.log('‚ùå Forbidden - –Ω–µ–¥–æ—Å—Ç–∞—Ç–æ—á–Ω–æ –ø—Ä–∞–≤');
                    break;
                case 404:
                    console.log('‚ùå Not Found - endpoint –Ω–µ —Å—É—â–µ—Å—Ç–≤—É–µ—Ç');
                    break;
                case 500:
                    console.log('‚ùå Server Error - –æ—à–∏–±–∫–∞ –Ω–∞ —Å–µ—Ä–≤–µ—Ä–µ');
                    break;
                default:
                    console.log('‚ùå Unknown error status');
            }
        } else if (error.request) {
            // –ó–∞–ø—Ä–æ—Å –±—ã–ª –æ—Ç–ø—Ä–∞–≤–ª–µ–Ω, –Ω–æ –æ—Ç–≤–µ—Ç–∞ –Ω–µ –±—ã–ª–æ
            console.log('‚ùå No response from server');
            console.log('Request:', error.request);
            console.log('Possible reasons:');
            console.log('  1. Backend is not running');
            console.log('  2. CORS issue');
            console.log('  3. Network error');
            console.log('  4. Wrong API URL:', API_URL);
        } else {
            // –û—à–∏–±–∫–∞ –ø—Ä–∏ –Ω–∞—Å—Ç—Ä–æ–π–∫–µ –∑–∞–ø—Ä–æ—Å–∞
            console.log('‚ùå Error setting up request');
            console.log('Message:', error.message);
        }

        return Promise.reject(error);
    }
);

// API –º–µ—Ç–æ–¥—ã
const apiService = {
    // Health check
    checkHealth: async () => {
        try {
            console.log('%cüè• Health Check', 'color: #FF9800; font-weight: bold;');
            const response = await api.get('/health');
            return { success: true, data: response.data };
        } catch (error) {
            console.error('Health check failed:', error.message);
            return { success: false, error: error.message };
        }
    },

    // –í –º–µ—Ç–æ–¥–µ login –≤ api.js –¥–æ–ª–∂–Ω–æ –±—ã—Ç—å:
    login: async (email, password) => {
        try {
            console.log('üîë Attempting login for:', email);
            const response = await api.post('/auth/login', { email, password });

            console.log('üì• Login response:', response.data);

            // –°–æ—Ö—Ä–∞–Ω—è–µ–º —Ç–æ–∫–µ–Ω –∏ –¥–∞–Ω–Ω—ã–µ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
            if (response.data.token) {
                localStorage.setItem('token', response.data.token);
                localStorage.setItem('user', JSON.stringify(response.data.user));

                console.log('‚úÖ Token saved to localStorage');
                console.log('‚úÖ User saved to localStorage');

                // –ü–†–û–í–ï–†–ö–ê: –°—Ä–∞–∑—É —á–∏—Ç–∞–µ–º –æ–±—Ä–∞—Ç–Ω–æ
                const savedToken = localStorage.getItem('token');
                const savedUser = localStorage.getItem('user');
                console.log('üîç Verification - token exists:', !!savedToken);
                console.log('üîç Verification - user exists:', !!savedUser);
            } else {
                console.warn('‚ö†Ô∏è No token in response!');
            }

            return { success: true, data: response.data };
        } catch (error) {
            // ... error handling
        }
    },

    // –í—ã—Ö–æ–¥
    logout: () => {
        console.log('%cüëã LOGOUT', 'color: #FF9800; font-weight: bold;');
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        window.location.href = '/login';
    },

    // –ü–æ–ª—É—á–µ–Ω–∏–µ –¥–∞–Ω–Ω—ã—Ö dashboard
    getDashboard: async () => {
        try {
            const response = await api.get('/dashboard');
            return { success: true, data: response.data };
        } catch (error) {
            return {
                success: false,
                error: error.response?.data?.message || '–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –¥–∞–Ω–Ω—ã—Ö'
            };
        }
    },

    // –ü–æ–ª—É—á–µ–Ω–∏–µ –∞—Ä—Ö–∏–≤–∞
    getArchive: async (params = {}) => {
        try {
            const response = await api.get('/archive', { params });
            return { success: true, data: response.data };
        } catch (error) {
            return {
                success: false,
                error: error.response?.data?.message || '–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –∞—Ä—Ö–∏–≤–∞'
            };
        }
    },

    // –£–Ω–∏–≤–µ—Ä—Å–∞–ª—å–Ω—ã–µ –º–µ—Ç–æ–¥—ã
    get: async (url, params = {}) => {
        try {
            const response = await api.get(url, { params });
            return { success: true, data: response.data };
        } catch (error) {
            return { success: false, error: error.message };
        }
    },

    post: async (url, data = {}) => {
        try {
            const response = await api.post(url, data);
            return { success: true, data: response.data };
        } catch (error) {
            return { success: false, error: error.message };
        }
    },

    put: async (url, data = {}) => {
        try {
            const response = await api.put(url, data);
            return { success: true, data: response.data };
        } catch (error) {
            return { success: false, error: error.message };
        }
    },

    delete: async (url) => {
        try {
            const response = await api.delete(url);
            return { success: true, data: response.data };
        } catch (error) {
            return { success: false, error: error.message };
        }
    }
};

export { api };
export default apiService;
